import React from "react";

const [busRoutes, setBusRoutes] = useState([])
const [user, setUser] = useState({})

useEffect (() => {
  fetch("/bus-routes/all").then(
    response => response.json()
  ).then(
    data => {
      setBusRoutes(data)
    }
  )
}, [])
const getBusRoutes = () => {
  fetch("/bus-routes/all").then(
    response => response.json()
  ).then(
    data => {
      setBusRoutes(data)
    }
  )
}

export function SideBar({busRoutes, getUsers})  {
  return(
    <div class="content">
      <header>
        <h2>Bus Routes</h2>
      </header>

      {busRoutes.map((busRoute, index) => 
        <button onClick={() => getUsers(index+1)}>{busRoute.name}</button>
      )}
    </div>
  )
}
