import React, { useEffect, useState } from 'react'
import Modal from 'react-modal';
import styles from './TabHeadings.module.scss';

const customStyles = {
  content: {
    top: '30%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};


export function TabHeadings()  {
  const userTypes = { 
    0 : 'STUDENT',
    1 : 'DRIVER'
  }

  let subtitle;
  const [modalIsOpen, setIsOpen] = useState(true);
  const [userType, setUserType] = useState(0);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    subtitle.style.display = "flex"

  }

  function closeModal() {
    setIsOpen(false);
  }

  return(
    <div className={styles.TabHeadingsDiv}>
      <div className={styles.Tabs}>
        <span className={styles.Tab} onClick={openModal}>ADD USER</span>
        {/* <span className={styles.Tab}>Delete User</span> */}
      </div>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <div ref={(_subtitle) => (subtitle = _subtitle)} className={styles.ModalTabs}>
          {Object.keys(userTypes).map((index) =>
            <div>{userTypes[index]}</div>
          )}
        </div>
        <form>
          <input />
        </form>
        <button onClick={() => closeModal()}>Close</button>
      </Modal>
    </div>
  )
}
