import React, { useEffect, useState } from 'react'
import MyModal from '../Modal/MyModal';
import cx from 'classnames';
import styles from './TabHeadings.module.scss';

export function TabHeadings({busRouteIndex}) {
  const userTypes = { 
    0 : 'STUDENT',
    1 : 'DRIVER'
  }

  const [modalIsOpen, setIsOpen] = useState(false);
  const [userKind, setUserKind] = useState(0);

  function openModal() {
    setIsOpen(!modalIsOpen);
  }

  return(
    <div className={styles.TabHeadingsDiv}>
      <div className={styles.Tabs}>
         <div></div> 
        <span className={styles.Tab} onClick={openModal}>ADD USER</span>
        <span className={styles.Tab}>Delete User</span>
      </div>
      {modalIsOpen && (
      <div className={styles.Modal}>
        <div className={styles.ModalTabs}>
          {Object.keys(userTypes).map((index) =>
            <div 
              className={userKind == index ? styles.ActiveModalTab : styles.ModalTab}
              onClick={() => setUserKind(index)}
            >{userTypes[index]}</div>
          )}
        </div>
        <form>
          <div className={styles.formLabel}><label>Name:</label><input /></div>
          <div className={styles.formLabel}><label>Contact:</label><input /></div>
          <div className={styles.formLabel}><label>Role:</label><input value={userTypes[userKind]}/></div>
          <div className={styles.formLabel}><label>busRoute:</label><input value={busRouteIndex}/></div>
        </form>
      </div>
      )}
    </div>
  )
}
