import React from 'react';
import cx from 'classnames';
import {TabButton} from '../TabButton/TabButton';
import styles from './SideBar.module.scss';
import Api  from '../../../utils/Api'

export function SideBar({busRoutes, getBusRouteUser, busRouteIndex})  {
  return(
    <div className={styles.sideBarDiv}>
      <header>
        <h2>Bus Routes</h2>
      </header>

      {busRoutes.map((busRoute, index) => 
        <button 
         onClick={() => getBusRouteUser(index+1)} 
         className={busRouteIndex == index+1 ? styles.activeButton : ''}>
          Bus Route {busRoute.routeNumber}
        </button>
      )}

      <TabButton label={"Add New Bus Route"} clickFuncion={''}/>
    </div>
  )
}
