import React, { useEffect, useState } from 'react'
import {TabButton} from '../TabButton/TabButton';
// import {Modal} from '../Modal/Modal';
import Modal from 'react-modal';
import styles from './Headings.module.scss';

export function Headings({busRouteIndex}) {
  let subtitle;
  const userTypes = { 
    0 : 'STUDENT',
    1 : 'DRIVER'
  }

  const [modalIsOpen, setIsOpen] = useState(false);
  const [userKind, setUserKind] = useState(0);

  function openModal() {
    setIsOpen(!modalIsOpen);
  }

  const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      padding: '50px 30px 30px 30px',
      width: '1000px'
    },
  };

  return(
    <div className={styles.headingsDiv}>
      <div className={styles.heading}>
        <h2 className={styles.pageHeading}>Bus Route {busRouteIndex}</h2> 
      </div>
      <div className={styles.tabs}>
        <TabButton className={styles.tab} label={"ADD USER"} clickFunction={openModal}/>
        <TabButton className={styles.tab} label={"DELETE BUS ROUTE"} clickFunction={openModal}/>
      </div>
      <Modal
        isOpen={true}
        // onAfterOpen={afterOpenModal}
        // onRequestClose={closeModal}
        style={customStyles}
        // contentLabel="Example Modal"
      >
        <div className={styles.headingModal}>
          {Object.keys(userTypes).map((index) =>
            <TabButton 
              label={userTypes[index]} 
              clickFunction={() => setUserKind(index)}
              active={userKind == index}
            />
          )}
          <form>
            <div className={styles.formLabel}><label>Name:</label><input /></div>
            <div className={styles.formLabel}><label>Contact:</label><input /></div>
            <div className={styles.formLabel}><label>Role:</label><input value={userTypes[userKind]}/></div>
            <div className={styles.formLabel}><label>busRoute:</label><input value={busRouteIndex}/></div>
          </form>
        </div>
      </Modal>
    </div>
  )
}
