import React, { useEffect, useState } from 'react'
import { SideBar } from '../SideBar/SideBar'
import { MainSection } from '../MainSection/MainSection'

export function HomePage({}) {
    const [busRouteIndex, setBusRouteIndex] = useState([])
    const [busRoutes, setBusRoutes] = useState([])
    const [userData, setUserData] = useState([])

    useEffect (() => {
      fetch("/bus-routes/all").then(
        response => response.json()
      ).then(
        data => {
          setBusRoutes(data);
          getBusRouteUser(1);
        }
      )
    }, [])

    const getBusRouteUser = (index) => {
      setBusRouteIndex(index)
      fetch("/user/bus-route/"+index).then(
        response => response.json()
      ).then(
        data => {
          setUserData(data)
        }
      )
    }

    return(
      <div>
        <body class="is-preload">
          <div id="wrapper">
            <div id="main">
              <div class="inner"> 
                <header id="header">
                  <a href="index.html" class="logo"><strong>My School Bus</strong></a>
                </header>
                <section>
                  <SideBar busRoutes={busRoutes} getBusRouteUser={getBusRouteUser}/>
                  {userData && (<MainSection userData={userData}/>)}
                </section>
              </div>
            </div>
          </div>
          <script src="../assets/js/jquery.min.js"></script>
          <script src="../assets/js/browser.min.js"></script>
          <script src="../assets/js/breakpoints.min.js"></script>
          <script src="../assets/js/util.js"></script>
          <script src="../assets/js/main.js"></script>
        </body>
      </div>
    )
}
