import React from "react";
import styles from './MainSection.module.scss';

export function MainSection({userData})  {

  const students = userData.filter((user) => user.role === 'student')
  const drivers = userData.filter((user) => user.role === 'driver')

  const printUserList = (user, label) => {
    return(
      <div className={styles.list}>
        <div><span>Student :</span> {user.name}</div>
        <div><span>{label} :</span> {user.role}</div>
      </div>
    )
  }
  return(
    <div className={styles.mainSectionDiv}>
      <div className={styles.section}>
        { students.map((student) => {
          return(printUserList(student))
        })}
      </div>
      <div className={styles.section}>
        { drivers.map((driver) => {
          return(
            <div className={styles.list}>
              <div><span>Driver :</span> {driver.name}</div>
              <div><span>Contact :</span> {driver.role}</div>
            </div>
          )
        })}
      </div>
    </div>
  )
}
