import React, { useEffect, useState } from 'react'
import { BusRouteList } from './BusRouteList'

export function HomePage({}) {
    const [busRoutes, setBusRoutes] = useState([])
    const [user, setUser] = useState({})

    useEffect (() => {
      fetch("/bus-routes/all").then(
        response => response.json()
      ).then(
        data => {
          setBusRoutes(data)
        }
      )
    }, [])

    const getUsers = (index) => {
      fetch("/user/byId/"+index).then(
        response => response.json()
      ).then(
        data => {
          setUser(data)
        }
      )
    }

    const getBusRoutes = () => {
      fetch("/bus-routes/all").then(
        response => response.json()
      ).then(
        data => {
          setBusRoutes(data)
        }
      )
    }

    return(
      <div>
          <head>  
            <title>MySchoolBus</title>
            <meta charset="utf-8" />
            <meta name="viewport" content="width=device-width, initial-scale=1, user-scalable=no" />
            <link rel="stylesheet" href="assets/css/main.css" />
          </head>
        	<body class="is-preload">
            <div id="wrapper">
                <div id="main">
                  <div class="inner">            
                      <header id="header">
                        <a href="index.html" class="logo"><strong>My School Bus</strong></a>
                      </header>
                      <section>
                        <div class="content">
                          <header>
                            <h2>Bus Routes</h2>
                          </header>
                          <BusRouteList busRoutes={busRoutes}></BusRouteList>
                          {busRoutes.map((busRoute, index) => 
                            <button onClick={() => getUsers(index+1)}>{busRoute.name}</button>
                          )}
                        </div>
                        <div>
                          <div>{user.name}</div>
                          <div>{user.email}</div>
                          <div>{user.role}</div>
                        </div>
                      </section>
                  </div>
                </div>
            </div>
            <script src="../assets/js/jquery.min.js"></script>
            <script src="../assets/js/browser.min.js"></script>
            <script src="../assets/js/breakpoints.min.js"></script>
            <script src="../assets/js/util.js"></script>
            <script src="../assets/js/main.js"></script>
          </body>
      </div>
    )
}
