import React, { useEffect, useState } from 'react'
import { SideBar } from './SideBar'
import { MainSection } from './MainSection'

export function HomePage({}) {
    const [busRoutes, setBusRoutes] = useState([])
    const [user, setUser] = useState({})

    useEffect (() => {
      fetch("/bus-routes/all").then(
        response => response.json()
      ).then(
        data => {
          setBusRoutes(data)
        }
      )
    }, [])

    const getUsers = (index) => {
      fetch("/user/byId/"+index).then(
        response => response.json()
      ).then(
        data => {
          setUser(data)
        }
      )
    }

    return(
      <div>
        <body class="is-preload">
          <div id="wrapper">
            <div id="main">
              <div class="inner">            
                <header id="header">
                  <a href="index.html" class="logo"><strong>My School Bus</strong></a>
                </header>
                <section>
                  <SideBar busRoutes={busRoutes} getUsers={getUsers}/>
                  {user.length && (<MainSection user={user}/>)}
                </section>
              </div>
            </div>
          </div>
          <script src="../assets/js/jquery.min.js"></script>
          <script src="../assets/js/browser.min.js"></script>
          <script src="../assets/js/breakpoints.min.js"></script>
          <script src="../assets/js/util.js"></script>
          <script src="../assets/js/main.js"></script>
        </body>
      </div>
    )
}
