import React, { useEffect, useState } from 'react'
import Modal from 'react-modal';
import styles from './TabHeadings.module.scss';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};


export function TabHeadings()  {
  const userTypes = { 
    1 : 'student',
    2 : 'driver'
  }

  let subtitle;
  const [modalIsOpen, setIsOpen] = useState(false);
  const [userType, setUserType] = useState(1);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  function closeModal() {
    setIsOpen(false);
  }

  return(
    <div className={styles.TabHeadingsDiv}>
     <div className={styles.Tabs}>
        <span className={styles.Tab} onClick={openModal}>ADD USER</span>
        {/* <span className={styles.Tab}>Delete User</span> */}
        <Modal
          isOpen={modalIsOpen}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 ref={(_subtitle) => (subtitle = _subtitle)}>ADD USER</h2>
          <button onClick={closeModal}>close</button>
          {Object.keys(userTypes).map((user) => {
            <div>{user}</div>
          })}
          {/* {console.log(Object.keys(userTypes))} */}
          <form>
            <input />
            <button>tab navigation</button>
            <button>stays</button>
            <button>inside</button>
            <button>the modal</button>
          </form>
        </Modal>
      </div>
    </div>
  )
}
